func (r *HAAudit) _validateCRD() error {
	if r.Spec.Targets == nil || len(r.Spec.Targets) == 0 {
		return fmt.Errorf("targets cannot be nil")
	}
	for _, target := range r.Spec.Targets {
		if target.Name == "" && target.Namespace == "" && target.NameRegex == "" {
			return fmt.Errorf("target name cannot be empty")
		}
		if target.Kind == "" {
			return fmt.Errorf("target kind cannot be empty")
		}
		if target.Namespace == "" {
			return fmt.Errorf("target namespace cannot be empty")
		}
	}

	if !reflect.DeepEqual(r.Spec.Report.PrometheusReport, HAReport{}) {
		match, err := regexp.MatchString(RELATIVE_URL_REGEX, r.Spec.Report.PrometheusReport.Address)
		if err != nil {
			kernel.Logger.Error(err, "error while validating prometheus address")
		}
		if !match {
			return fmt.Errorf("prometheus address is not valid")
		}
	}
	return nil
}
